import React, { useState } from 'react';

interface Answer {
    text: string;
    isCorrect: boolean;
}

interface Question {
    question: string;
    answers: Answer[];
}

interface QuizProps {
    questions: Question[];
}

const Quiz: React.FC<QuizProps> = ({ questions }) => {
    const [selectedAnswers, setSelectedAnswers] = useState<(boolean | null)[]>(Array(questions.length).fill(null));
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [correctCount, setCorrectCount] = useState<number | null>(null);

    const handleAnswerClick = (isCorrect: boolean) => {
        const updatedAnswers = [...selectedAnswers];
        updatedAnswers[currentQuestionIndex] = isCorrect;
        setSelectedAnswers(updatedAnswers);

        if (isCorrect) {
            setCorrectCount((prev) => (prev !== null ? prev + 1 : 1));
        }
    };

    const nextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
        }
    };

    const calculateScore = () => {
        setCorrectCount(selectedAnswers.filter((answer) => answer === true).length);
    };

    return (
        <div style={styles.container}>
            <h2 style={styles.questionHeader}>
                Question {currentQuestionIndex + 1} of {questions.length}
            </h2>
            <h3 style={styles.questionText}>{questions[currentQuestionIndex].question}</h3>
            <ul style={styles.answerList}>
                {questions[currentQuestionIndex].answers.map((answer, index) => (
                    <li key={index} style={styles.answerItem}>
                        <button
                            onClick={() => handleAnswerClick(answer.isCorrect)}
                            style={{
                                ...styles.answerButton,
                                backgroundColor:
                                    selectedAnswers[currentQuestionIndex] !== null
                                        ? answer.isCorrect
                                            ? 'green'
                                            : 'lightgrey'
                                        : 'lightgrey',
                                color: selectedAnswers[currentQuestionIndex] !== null ? '#fff' : '#000',
                            }}
                            disabled={selectedAnswers[currentQuestionIndex] !== null}
                        >
                            {answer.text}
                        </button>
                    </li>
                ))}
            </ul>
            {currentQuestionIndex < questions.length - 1 ? (
                <button style={styles.nextButton} onClick={nextQuestion} disabled={selectedAnswers[currentQuestionIndex] === null}>
                    Next
                </button>
            ) : (
                <button style={styles.nextButton} onClick={calculateScore}>
                    Show Results
                </button>
            )}

            <div style={styles.progressContainer}>
                {questions.map((_, index) => (
                    <div
                        key={index}
                        style={{
                            ...styles.progressCircle,
                            ...(selectedAnswers[index] === true ? styles.greenCircle : {}),
                        }}
                    >
                        {selectedAnswers[index] === true ? '✓' : ''}
                    </div>
                ))}
            </div>

            {correctCount !== null && (
                <h4 style={styles.resultText}>
                    You got {correctCount} out of {questions.length} correct!
                </h4>
            )}
        </div>
    );
};

const styles = {
    container: {
        padding: '20px',
        width: '500px',
        margin: '0 auto',
    },
    questionHeader: {
        color: 'grey',
        marginBottom: '10px',
    },
    questionText: {
        fontWeight: 'bold',
        marginBottom: '20px',
    },
    answerList: {
        listStyleType: 'none',
        paddingLeft: '0',
    },
    answerItem: {
        marginBottom: '10px',
        color: black,
    },
    answerButton: {
        width: '100%',
        padding: '10px',
        fontSize: '16px',
        border: '1px solid grey',
        borderRadius: '5px',
        textAlign: 'left' as const,
        cursor: 'pointer',
        display: 'block',
    },
    nextButton: {
        marginTop: '20px',
        padding: '10px 20px',
        backgroundColor: 'green',
        color: 'white',
        border: 'none',
        borderRadius: '5px',
    },
    resultText: {
        marginTop: '20px',
        fontSize: '18px',
        color: 'green',
    },
    progressContainer: {
        display: 'flex',
        justifyContent: 'center',
        marginTop: '20px',
    },
    progressCircle: {
        width: '30px',
        height: '30px',
        borderRadius: '50%',
        backgroundColor: 'lightgrey',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        margin: '0 5px',
        fontSize: '18px',
        color: 'white', 
        fontWeight: 'bold',
        border: '2px solid transparent',
    },
    greenCircle: {
        backgroundColor: 'green',
        color: 'white',
    }
};

export default Quiz;
