import React, { useState } from 'react';
import '../styles/styles.css';

interface Answer {
    text: string;
    isCorrect: boolean;
}

interface Question {
    question: string;
    answers: Answer[];
}

interface QuizProps {
    questions: Question[];
}

const Quiz: React.FC<QuizProps> = ({ questions }) => {
    const [selectedAnswer, setSelectedAnswer] = useState<boolean | null>(null);
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [correctCount, setCorrectCount] = useState<number | null>(null);

    const handleAnswerClick = (isCorrect: boolean) => {
        setSelectedAnswer(isCorrect); // Store the result of the clicked answer

        if (isCorrect) {
            setCorrectCount((prev) => (prev !== null ? prev + 1 : 1));
        } else {
            setCorrectCount((prev) => (prev !== null ? prev : 0)); // Count incorrect answer only if not set before
        }
    };

    const nextQuestion = () => {
        if (currentQuestionIndex < questions.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setSelectedAnswer(null); // Reset the selected answer for the next question
        }
    };

    const calculateScore = () => {
        setCorrectCount(selectedAnswer === true ? correctCount : correctCount); // Ensure correct count is calculated
    };

    return (
        <div className='container'>
            <h2 className='questionHeader'>
                Question {currentQuestionIndex + 1} of {questions.length}
            </h2>
            <hr />
            <h3 className='questionText'>{questions[currentQuestionIndex].question}</h3>
            <ul className='answerList'>
                {questions[currentQuestionIndex].answers.map((answer, index) => (
                    <li key={index} className='answerItem'>
                        <button
                            onClick={() => handleAnswerClick(answer.isCorrect)}
                            className='answerButton'
                            style={{
                                backgroundColor:
                                    selectedAnswer === null
                                        ? 'lightgrey'
                                        : answer.isCorrect && selectedAnswer
                                            ? 'green'
                                            : !answer.isCorrect && !selectedAnswer
                                                ? 'red'
                                                : 'lightgrey',
                                color: 'white',
                            }}
                            disabled={selectedAnswer !== null}
                        >
                            {answer.text}
                        </button>
                    </li>
                ))}
            </ul>
            {currentQuestionIndex < questions.length - 1 ? (
                <button className='nextButton' onClick={nextQuestion} disabled={selectedAnswer === null}>
                    Next
                </button>
            ) : (
                <button className='nextButton' onClick={calculateScore}>
                    Show Results
                </button>
            )}

            <div className='progressContainer'>
                {questions.map((_, index) => (
                    <div
                        key={index}
                        className={`progressCircle ${selectedAnswer === true ? 'greenCircle' : ''}`}
                    >
                        {selectedAnswer === true ? '✓' : ''}
                    </div>
                ))}
            </div>

            {correctCount !== null && (
                <h4 className='resultText'>
                    You got {correctCount} out of {questions.length} correct!
                </h4>
            )}
        </div>
    );
};

export default Quiz;
